sysauto_script.script:
// Get today's time and date
var rightnow = new GlideDateTime();


// Query the database for NeedIt Task REcords with Due date value field older
// than the current time. Only return NeedIt Task Records that do not have
// a State field value of closed complete.
var duesoon = new GlideRecord('x_58872_needit_needit_task');
duesoon.addQuery('due_date', '>=', rightnow);
duesoon.addQuery('due_date', '<',gs.hoursAgo(-24));
duesoon.addQuery('state', '!=', 3);

duesoon.query();
// Write log message for each overdue NeedIt Task Record
while(duesoon.next()){
	gs.info("NeedIt Task duesoon record = " + duesoon.number);
	gs.eventQueue('x_58872_needit.overdueNeeditTask', overdueNITasks,overdueNITasks.number,gs.getUserName());
}
